                      (                          SELECT MIN(SL)                          FROM (                                  SELECT COUNT(MATUYEN) SL                                  FROM BTKHDL1.VEXE                                  WHERE EXTRACT (YEAR FROM NGMUA) = 2016                                  GROUP BY MATUYEN                                )                        );/* CAU 7 */SELECT BTKHDL1.TUYEN.MATUYENFROM BTKHDL1.TUYEN, BTKHDL1.KHACH, BTKHDL1.VEXEWHERE VEXE.MAHK = KHACH.MAHK AND VEXE.MATUYEN = TUYEN.MATUYENAND GIOITINH = 'Nam'INTERSECTSELECT BTKHDL1.TUYEN.MATUYENFROM BTKHDL1.TUYEN, BTKHDL1.KHACH, BTKHDL1.VEXEWHERE VEXE.MAHK = KHACH.MAHK AND VEXE.MATUYEN = TUYEN.MATUYENAND GIOITINH='Nu';/* Cau 8 */SELECT * FROM BTKHDL1.KHACH HKWHERE GIOITINH='Nu' AND NOT EXISTS (                                      SELECT * FROM  BTKHDL1.TUYEN T                                      WHERE NOT EXISTS (                                                        SELECT * FROM  BTKHDL1.VEXE VX                                                        WHERE T.MATUYEN = VX.MATUYEN AND HK.MAHK = VX.MAHK                                                        )                                    );